-- v18 deleted this column for some reason...
alter table mission add column create_time timestamp(3) with time zone NOT NULL;
alter table mission add column tool varchar;
alter table resource add column tool varchar;
 


CREATE TABLE mission_subscription (
       mission_id bigint NOT NULL,
       client_uid text NOT NULL,
       create_time timestamp(3) with time zone NOT NULL
);

ALTER TABLE ONLY mission_subscription ADD CONSTRAINT mission_subscription_pkey PRIMARY KEY (mission_id, client_uid);
-- add group column in resource and mission tables
alter table resource add column groups bit varying;
alter table mission add column groups bit varying;

-- backfill group membership in resource and mission
update resource set groups = '';

update mission set groups = '';


CREATE OR REPLACE VIEW latestresource AS 
    SELECT resource.id, resource.altitude, resource.data, resource.filename, resource.keywords,
           resource.location, resource.mimetype, resource.name, resource.permissions, resource.remarks, 
           resource.submissiontime, resource.submitter, resource.uid, resource.hash, resource.groups
    FROM resource 
    JOIN ( SELECT resource.uid, max(resource.submissiontime) AS latestupload 
           FROM resource GROUP BY resource.uid) groupedresource 
    ON resource.uid::text = groupedresource.uid::text 
    AND resource.submissiontime = groupedresource.latestupload 
    ORDER BY resource.id;

DROP TABLE IF EXISTS public.ci_trap;
CREATE TABLE public.ci_trap
(
  id integer NOT NULL,
  type character varying,
  user_callsign character varying,
  user_description character varying,
  date_time timestamp with time zone,
  date_time_description character varying,
  location_description character varying,
  event_scale character varying,
  importance character varying,
  uid character varying,
  location geometry,
  hash character varying,
  xml character varying,
  groups bit varying
);
  
DROP SEQUENCE IF EXISTS ci_trap_id_seq;
CREATE SEQUENCE ci_trap_id_seq
  START WITH 1
  INCREMENT BY 1 
  NO MINVALUE 
  NO MAXVALUE 
  CACHE 1;   

ALTER SEQUENCE ci_trap_id_seq OWNED BY ci_trap.id;
ALTER TABLE ci_trap ALTER COLUMN id SET DEFAULT nextval('ci_trap_id_seq'::regclass);
ALTER TABLE ONLY ci_trap ADD CONSTRAINT ci_trap_pkey PRIMARY KEY (id);alter table ci_trap add column title character varying;
